mapper.update:
UPDATE projects
name = #{project.name}, description = #{project.description},
WHERE idx = #{idx}

mapper.select:
select result from (
	SELECT IFNULL(idx,0) as result, count(idx) as cnt FROM projects
	WHERE
		0 = 0

			 and idx = #{idx}


			 and name = #{keyword}

) as a

SELECT idx, name, description , reg_date FROM projects
WHERE
	0 = 0

		 and idx = #{idx}


		 and name = #{name}

SELECT name, description, group_id, artifact_id, package, reg_date, build_profile_idx FROM projects
where name like '%${keyword}%' or description like '%${keyword}%'
order by
${order_column} ${order_dir}
idx desc
limit #{offset}, #{limit}
